// SPDX-License-Identifier: MIT
pragma solidity >=0.5.0;

contract AdManager {
    int public adCount = 0;
    int public userCount = 0;

    mapping(int => User) public users;
    mapping(int => Ad) public advertisements;

    struct Ad {
        int ownerId;
        int id;
        string content;
        int adCount;
        int viewCount;
        mapping(int => int) viewers;
    }

    struct User {
        int userId;
        int numAds;
    }

    function createUser() public {
        userCount++;
        User memory newUser = User(userCount, 0);
        users[userCount] = newUser;
    }

    function createAd(int owner_id, string memory content, int count) public {
        adCount++;

        Ad storage newAd = advertisements[adCount];
        newAd.ownerId = owner_id;
        newAd.content = content;
        newAd.adCount = count;
        newAd.id = adCount;
        newAd.viewCount = 0;
    }

    function addViewer(int adId,  int userId) public {
        Ad storage a = advertisements[adId];
        a.viewCount++;
        a.viewers[a.viewCount] = userId;
    }

    function getViewerId(int adId, int index) public view returns (int) {
        Ad storage ad = advertisements[adId];
        int userId = ad.viewers[index];
        return userId;
    }

}
